(252, '_site_transient_community-events-d41d8cd98f00b204e9800998ecf8427e', 'a:4:{s:9:\"sandboxed\";b:0;s:5:\"error\";N;s:8:\"location\";a:1:{s:2:\"ip\";b:0;}s:6:\"events\";a:5:{i:0;a:10:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:23:\"WordPress Meetup Zwolle\";s:3:\"url\";s:63:\"https://www.meetup.com/zwolle-wordpress-meetup/events/292679610\";s:6:\"meetup\";s:23:\"WordPress Meetup Zwolle\";s:10:\"meetup_url\";s:47:\"https://www.meetup.com/Zwolle-WordPress-Meetup/\";s:4:\"date\";s:19:\"2023-05-11 18:45:00\";s:8:\"end_date\";s:19:\"2023-05-11 20:45:00\";s:20:\"start_unix_timestamp\";i:1683823500;s:18:\"end_unix_timestamp\";i:1683830700;s:8:\"location\";a:4:{s:8:\"location\";s:19:\"Zwolle, Netherlands\";s:7:\"country\";s:2:\"nl\";s:8:\"latitude\";d:52.508583;s:9:\"longitude\";d:6.093302;}}i:1;a:10:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:21:\"WordPress Meetup 010 \";s:3:\"url\";s:66:\"https://www.meetup.com/rotterdam-wordpress-meetup/events/292890878\";s:6:\"meetup\";s:20:\"WordPress Meetup 010\";s:10:\"meetup_url\";s:50:\"https://www.meetup.com/Rotterdam-WordPress-Meetup/\";s:4:\"date\";s:19:\"2023-05-22 19:00:00\";s:8:\"end_date\";s:19:\"2023-05-22 22:00:00\";s:20:\"start_unix_timestamp\";i:1684774800;s:18:\"end_unix_timestamp\";i:1684785600;s:8:\"location\";a:4:{s:8:\"location\";s:22:\"Rotterdam, Netherlands\";s:7:\"country\";s:2:\"nl\";s:8:\"latitude\";d:51.90691;s:9:\"longitude\";d:4.460783;}}i:2;a:10:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:19:\"WP Meetup Amsterdam\";s:3:\"url\";s:59:\"https://www.meetup.com/wordpress-amsterdam/events/287189145\";s:6:\"meetup\";s:26:\"WordPress Meetup Amsterdam\";s:10:\"meetup_url\";s:43:\"https://www.meetup.com/wordpress-amsterdam/\";s:4:\"date\";s:19:\"2023-05-23 19:00:00\";s:8:\"end_date\";s:19:\"2023-05-23 21:00:00\";s:20:\"start_unix_timestamp\";i:1684861200;s:18:\"end_unix_timestamp\";i:1684868400;s:8:\"location\";a:4:{s:8:\"location\";s:22:\"Amsterdam, Netherlands\";s:7:\"country\";s:2:\"nl\";s:8:\"latitude\";d:52.36531;s:9:\"longitude\";d:4.935536;}}i:3;a:10:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:36:\"WordPress Meetup Fryslân – 25 mei\";s:3:\"url\";s:64:\"https://www.meetup.com/wordpress-meetup-fryslan/events/290420211\";s:6:\"meetup\";s:25:\"WordPress Meetup Fryslân\";s:10:\"meetup_url\";s:48:\"https://www.meetup.com/WordPress-Meetup-Fryslan/\";s:4:\"date\";s:19:\"2023-05-25 19:00:00\";s:8:\"end_date\";s:19:\"2023-05-25 21:00:00\";s:20:\"start_unix_timestamp\";i:1685034000;s:18:\"end_unix_timestamp\";i:1685041200;s:8:\"location\";a:4:{s:8:\"location\";s:27:\"Leeuwarden, FR, Netherlands\";s:7:\"country\";s:2:\"NL\";s:8:\"latitude\";d:53.21;s:9:\"longitude\";d:5.79;}}i:4;a:10:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:45:\"WordPress Coworking sessie Fryslân - 27 juni\";s:3:\"url\";s:64:\"https://www.meetup.com/wordpress-meetup-fryslan/events/290420239\";s:6:\"meetup\";s:25:\"WordPress Meetup Fryslân\";s:10:\"meetup_url\";s:48:\"https://www.meetup.com/WordPress-Meetup-Fryslan/\";s:4:\"date\";s:19:\"2023-06-27 10:00:00\";s:8:\"end_date\";s:19:\"2023-06-27 12:00:00\";s:20:\"start_unix_timestamp\";i:1687852800;s:18:\"end_unix_timestamp\";i:1687860000;s:8:\"location\";a:4:{s:8:\"location\";s:27:\"Leeuwarden, FR, Netherlands\";s:7:\"country\";s:2:\"NL\";s:8:\"latitude\";d:53.21;s:9:\"longitude\";d:5.79;}}}}', 'no'),
